import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [sqlQuery, setSqlQuery] = useState('');
  const [result, setResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResult([]);
    setSqlQuery('');

    try {
      const response = await axios.post('http://localhost:8000/execute_query/', { query });

      if (response.data.sql_query && Array.isArray(response.data.result)) {
        setSqlQuery(response.data.sql_query);
        setResult(response.data.result);
      } else {
        setError('Invalid response from server. Please check your query.');
      }
    } catch (error) {
      console.error('Error executing query:', error);
      setError(error.response?.data?.detail || 'Failed to execute query. Please try again.');
    }

    setLoading(false);
  };

  return (
    <div className="App">
      <h1>Text to SQL Query Execution</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter your natural language query here..."
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Executing...' : 'Execute Query'}
        </button>
      </form>

      {error && <p className="error">{error}</p>}

      {sqlQuery && (
        <div>
          <h2>Generated SQL Query:</h2>
          <pre>{sqlQuery}</pre>
        </div>
      )}

      {result.length > 0 && (
        <div>
          <h2>Result:</h2>
          <table border="1">
            <thead>
              <tr>
                {Object.keys(result[0]).map((key) => (
                  <th key={key}>{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {result.map((row, index) => (
                <tr key={index}>
                  {Object.values(row).map((value, idx) => (
                    <td key={idx}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
